import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FaArrowUp, FaArrowDown } from "react-icons/fa";
import "./HomePage.css"; // استدعاء ملف التنسيق

function HomePage() {
  const navigate = useNavigate();

  useEffect(() => {
    const handleScroll = (e) => {
      if (e.key === "ArrowDown") {
        window.scrollBy({ top: window.innerHeight, behavior: "smooth" });
      } else if (e.key === "ArrowUp") {
        window.scrollBy({ top: -window.innerHeight, behavior: "smooth" });
      }
    };
    window.addEventListener("keydown", handleScroll);
    return () => window.removeEventListener("keydown", handleScroll);
  }, []);

  return (
    <div className="container">
      {/* ✅ شريط التنقل */}
      <nav className="navbar">
        <div className="logo">🎤 PodText</div>
        <div className="nav-buttons">
          <button className="login-btn" onClick={() => navigate("/login")}>تسجيل الدخول</button>
          <button className="signup-btn">الاشتراك</button>
        </div>
      </nav>

      {/* ✅ القسم الرئيسي */}
      <section className="hero">
        <img src="/hero-image.png" alt="Hero" className="hero-image" />
        <h1>مرحبًا بك في PodText</h1>
        <p>
          يهدف هذا الموقع إلى مساعدة ذوي الاحتياجات الخاصة (الصم) في استخدام البرامج التعليمية وتحويل المحتوى الصوتي من MP3 إلى نص، بالإضافة إلى التلخيص والبحث وطرح الأسئلة المطلوبة على المحتوى.
        </p>
        <button className="start-btn" onClick={() => navigate("/login")}>ابدأ الآن</button>
      </section>

      {/* ✅ الميزات */}
      <section className="features">
        <h2>ميزاتنا الحالية</h2>
        <p>نؤمن بالتعليم المستمر والنمو، حيث نوفر حلولًا متطورة لمساعدة المستخدمين.</p>
        <div className="feature-boxes">
          <div className="feature-box">
            <h3>📂 تحميل الفيديو إلى نص</h3>
            <p>يمكنك تحميل أي فيديو وتحويله إلى نص مكتوب بسرعة.</p>
          </div>
          <div className="feature-box">
            <h3>📊 تحليل النص</h3>
            <p>تحليل النصوص لاستخراج المعلومات المهمة وتلخيص المحتوى.</p>
          </div>
          <div className="feature-box">
            <h3>🤖 سؤال وجواب</h3>
            <p>يمكنك طرح الأسئلة على الذكاء الاصطناعي حول المحتوى المستخرج.</p>
          </div>
        </div>
      </section>

      {/* ✅ التذييل (Footer) */}
      <footer className="footer">
        <p>© 2025 جميع الحقوق محفوظة - PodText</p>
        <div className="social-links">
          <a href="#">🔗 فيسبوك</a>
          <a href="#">🔗 تويتر</a>
          <a href="#">🔗 إنستجرام</a>
        </div>
      </footer>

      {/* ✅ أزرار التنقل لأعلى وأسفل */}
      <div className="scroll-buttons">
        <button onClick={() => window.scrollBy({ top: -window.innerHeight, behavior: "smooth" })}>
          <FaArrowUp />
        </button>
        <button onClick={() => window.scrollBy({ top: window.innerHeight, behavior: "smooth" })}>
          <FaArrowDown />
        </button>
      </div>
    </div>
  );
}

export default HomePage;
