import React from "react";
import { Link } from "react-router-dom";
import "./Home.css";

function Home() {
  return (
    <div className="home-container">
      <section className="intro">
        <h1>مرحبًا بك في PodText</h1>
        <p>
          يهدف هذا الموقع إلى مساعدة ذوي الاحتياجات الخاصة (الصم) 
          في استخدام البرامج التعليمية بتحويل محتوى الصوت في MP3 
          إلى نص مكتوب، مع إمكانية تلخيصه وطرح الأسئلة المطلوبة على المحتوى.
        </p>
        <Link to="/login">
          <button className="start-btn">ابدأ الآن</button>
        </Link>
      </section>
    </div>
  );
}

export default Home;
