import React from "react";
import "./Features.css";

function Features() {
  return (
    <section className="features">
      <div className="feature">
        <img src="/images/feature1.png" alt="Feature 1" />
        <h3>تحميل الفيديو إلى نص</h3>
        <p>يمكنك بسهولة تحويل أي فيديو إلى نص مكتوب بدقة.</p>
      </div>
      <div className="feature">
        <img src="/images/feature2.png" alt="Feature 2" />
        <h3>تحليل</h3>
        <p>يوفر لك تحليلاً دقيقًا للمحتوى الصوتي وتحويله إلى نص ذكي.</p>
      </div>
      <div className="feature">
        <img src="/images/feature3.png" alt="Feature 3" />
        <h3>سؤال وجواب</h3>
        <p>بعد تحويل المحتوى إلى نص، يمكنك طرح أسئلة حول المحتوى بسهولة.</p>
      </div>
    </section>
  );
}

export default Features;
