import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from "react-router-dom";
import { useEffect } from "react";

function HomePage() {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="container mx-auto p-4 text-center">
        <h1 className="text-3xl font-bold">مرحبًا بك في PodText</h1>
        <p className="mt-2">هذا الموقع يساعد في تحويل ملفات MP3 إلى نصوص</p>
        <button 
          className="mt-4 bg-blue-500 text-white px-6 py-2 rounded" 
          onClick={() => navigate('/login')}
        >ابدأ الآن</button>
      </div>
      <div className="container mx-auto p-4 mt-8">
        <h2 className="text-2xl font-semibold">ميزاتنا الحالية</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-4">
          <div className="bg-white p-4 rounded shadow">تحميل الفيديو إلى نص</div>
          <div className="bg-white p-4 rounded shadow">تحليل النص</div>
          <div className="bg-white p-4 rounded shadow">سؤال وجواب</div>
        </div>
      </div>
    </div>
  );
}

function LoginPage() {
  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded shadow-md w-96">
        <h2 className="text-2xl font-semibold text-center">تسجيل الدخول</h2>
        <input 
          type="email" 
          placeholder="البريد الإلكتروني" 
          className="w-full p-2 mt-4 border rounded"
        />
        <input 
          type="password" 
          placeholder="كلمة المرور" 
          className="w-full p-2 mt-4 border rounded"
        />
        <button className="w-full mt-4 bg-blue-500 text-white p-2 rounded">دخول</button>
      </div>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/login" element={<LoginPage />} />
      </Routes>
    </Router>
  );
}

export default App;
